function iw_patterns_register_pattern_categories() {
  register_block_pattern_category(
      'wooster',
    array( 'label' => __( 'Wooster Patterns', 'iw-patterns' ) )
      );
}
add_action( 'init', 'iw_patterns_register_pattern_categories' );

